let LegendsInstance = null;
module.exports = class yAxis {

    constructor() {
        if (!LegendsInstance) {
            LegendsInstance = this;
            //set all other default values

            this.displayAs = 'Show Legends';
            this.value = '[legend]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return LegendsInstance;
    }
}