let MatHintInstance = null;
module.exports = class MatHint {

    constructor() {
        if (!MatHintInstance) {
            MatHintInstance = this;
            this.value = 'inputHint'
            this.displayAs = 'Hints';
            this.type = 'LIST_DIALOG';
            this.values = {

            }
            this.title = "Configure Hints"
            this.model = {
                input1: {
                    placeholder: 'Hint Condition'
                },
                input2: {
                    placeholder: 'Message'
                },
                toggle: {
                toggleData: ['start', 'end'],
                selected: 'start',
                displayAs: 'Message align'

            }
        }
        return MatHintInstance;
    }
}
}
