let MatSortHeaderInstance = null;
module.exports = class MatSortHeader {

    constructor() {
        if (!MatSortHeaderInstance) {
            MatSortHeaderInstance = this;
            //set all other default values

            this.displayAs = 'Sortable';
            this.value = 'matSortHeader';
            this.type = 'TOGGLE';
            this.values = {'true-value': 'matSort', 'false-value': ''};
        }
        return MatSortHeaderInstance;
    }
}
