let MatSortHeaderColumnInstance = null;
module.exports = class MatSortHeaderColumn {
    constructor() {
        if (!MatSortHeaderColumnInstance) {
            MatSortHeaderColumnInstance = this;
            this.displayAs = 'Sort';
            this.value = 'matSortHeaderColumn';
            this.type = 'TOGGLE';
            this.values = {
                'true-value': 'mat-sort-header',
                'false-value': ''
            };
        }
        return MatSortHeaderColumnInstance;
    }
}
