let MatTabAlignInstance = null;
module.exports = class matTabAlign {

    constructor() {
        if (!MatTabAlignInstance) {
            MatTabAlignInstance = this;
            //set all other default values

            this.displayAs = 'Align Tabs';
            this.value = 'matAlignTabs'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Start", value: "start"},                
                { display: "Center", value: "center"},
                { display: "End", value: "end"}

            ];
        }

        return MatTabAlignInstance;
    }
}