let multipleInstance = null;
module.exports = class multiple {

    constructor() {
        if (!multipleInstance) {
            multipleInstance = this;
            //set all other default values

            this.displayAs = 'Multiple';
            this.value = '[multiple]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'};
        }
        return multipleInstance;
    }
}