let mulptipleSelectInstance = null;
module.exports = class multipleSelect {

    constructor() {
        if (!mulptipleSelectInstance) {
            mulptipleSelectInstance = this;
            //set all other default values

            this.displayAs = 'Multiple Select';
            this.value = 'multiple'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Multiple select', 'false-label': 'Single select'}
            this.values = {'true-value':'multiple', 'false-value': ''}
        }
        return mulptipleSelectInstance;
    }
}