let openedInstance = null;
module.exports = class opened {

    constructor() {
        if (!openedInstance) {
            openedInstance = this;
            //set all other default values

            this.displayAs = 'Opened';
            this.value = '[opened]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return openedInstance;
    }
}