let optionalInstance = null;
module.exports = class optional {

    constructor() {
        if (!optionalInstance) {
            optionalInstance = this;
            //set all other default values

            this.displayAs = 'Optional';
            this.value = 'optional'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return optionalInstance;
    }
}