let PLayoutAlignInstance = null;
module.exports = class PLayoutAlign {

    constructor() {
        if (!PLayoutAlignInstance) {
            PLayoutAlignInstance = this;
            //set all other default values

            this.displayAs = 'Perpendicular Direction';
            this.value = 'pLayoutAlign'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "none", value: "none" },
                { display: "start ", value: "start"},
                { display: "center", value: "center" },
                { display: "end", value: "end" },
				{ display: "stretch", value: "stretch", default: true },
				{ display: "space-around", value: "space-around" },
                { display: "space-between", value: "space-between" },
                { display: "baseline", value: "baseline" },
            ];
        }

        return PLayoutAlignInstance;
    }
}