let pieChartLabelInstance = null;
module.exports = class doughnut {

    constructor() {
        if (!pieChartLabelInstance) {
            pieChartLabelInstance = this;
            //set all other default values

            this.displayAs = 'Show Labels';
            this.value = 'pieChartLabels'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return pieChartLabelInstance;
    }
}