let lablePositionInstance = null;
module.exports = class labelPosition {

    constructor() {
        if (!lablePositionInstance) {
            lablePositionInstance = this;
            //set all other default values

            this.displayAs = 'Label Position';
            this.value = '[labelPosition]'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Before", value: "'before'"},
                { display: "After", value: "'after'" },

            ];
        }

        return lablePositionInstance;
    }
}