let removableInstance = null;
module.exports = class removable {

    constructor() {
        if (!removableInstance) {
            removableInstance = this;
            //set all other default values

            this.displayAs = 'Removable';
            this.value = '[removable]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return removableInstance;
    }
}