let requiredFieldInstance = null;
module.exports = class requiredField {

    constructor() {
        if (!requiredFieldInstance) {
            requiredFieldInstance = this;
            //set all other default values

            this.displayAs = 'Required';
            this.value = 'required'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return requiredFieldInstance;
    }
}