let SchemeTypeInstance = null;
module.exports = class schemeType {

    constructor() {
        if (!SchemeTypeInstance) {
            SchemeTypeInstance = this;
            //set all other default values

            this.displayAs = 'Scheme Type';
            this.value = '[schemeType]'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Ordinal", value: "'ordinal'", default: true },
                { display: "Linear ", value: "'linear'"}

            ];
        }

        return SchemeTypeInstance;
    }
}