let selectStyleInstance = null;
module.exports = class selectStyle {

    constructor() {
        if (!selectStyleInstance) {
            selectStyleInstance = this;
            //set all other default values

            this.displayAs = 'Select Style';
            this.value = 'selectStyle'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Basic', 'false-label': 'Native'}
            this.values = {'true-value':'Basic', 'false-value': 'Native'}
        }
        return selectStyleInstance;
    }
}