let selectableInstance = null;
module.exports = class selectable {

    constructor() {
        if (!selectableInstance) {
            selectableInstance = this;
            //set all other default values

            this.displayAs = 'Selectable';
            this.value = '[selectable]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return selectableInstance;
    }
}