let selectedInstance = null;
module.exports = class selected {

    constructor() {
        if (!selectedInstance) {
            selectedInstance = this;
            //set all other default values

            this.displayAs = 'Selected';
            this.value = '[selected]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return selectedInstance;
    }
}