let showAxisInstance = null;
module.exports = class showAxis {

    constructor() {
        if (!showAxisInstance) {
            showAxisInstance = this;
            //set all other default values

            this.displayAs = 'Show Axis';
            this.value = '[showAxis]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return showAxisInstance;
    }
}