let showDataLabelInstance = null;
module.exports = class showDataLabel {

    constructor() {
        if (!showDataLabelInstance) {
            showDataLabelInstance = this;
            //set all other default values

            this.displayAs = 'Show Data Label';
            this.value = '[showDataLabel]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return showDataLabelInstance;
    }
}