let showRefLabelInstance = null;
module.exports = class showRefLabels {

    constructor() {
        if (!showRefLabelInstance) {
            showRefLabelInstance = this;
            //set all other default values

            this.displayAs = 'Show Reference Label';
            this.value = '[showRefLabels]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return showRefLabelInstance;
    }
}