let showRefLinesInstance = null;
module.exports = class showRefLines {

    constructor() {
        if (!showRefLinesInstance) {
            showRefLinesInstance = this;
            //set all other default values

            this.displayAs = 'Show Reference Lines';
            this.value = '[showRefLines]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return showRefLinesInstance;
    }
}