let invertInstance = null;
module.exports = class invert {

    constructor() {
        if (!invertInstance) {
            invertInstance = this;
            //set all other default values

            this.displayAs = 'Invert';
            this.value = 'invert'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return invertInstance;
    }
}