let verticalInstance = null;
module.exports = class vertical {

    constructor() {
        if (!verticalInstance) {
            verticalInstance = this;
            //set all other default values

            this.displayAs = 'Vertical';
            this.value = '[vertical]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return verticalInstance;
    }
}