let stepperLinearInstance = null;
module.exports = class linear {

    constructor() {
        if (!stepperLinearInstance) {
            stepperLinearInstance = this;
            //set all other default values

            this.displayAs = 'Linear';
            this.value = '[linear]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return stepperLinearInstance;
    }
}