let TimeLineInstance = null;
module.exports = class timeline {

    constructor() {
        if (!TimeLineInstance) {
            TimeLineInstance = this;
            //set all other default values

            this.displayAs = 'Timeline';
            this.value = '[timeline]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return TimeLineInstance;
    }
}