let TooltipPositionInstance = null;
module.exports = class tooltipPosition {

    constructor() {
        if (!TooltipPositionInstance) {
            TooltipPositionInstance = this;
            //set all other default values

            this.displayAs = 'Tooltip Position';
            this.value = 'matTooltipPosition'
            this.type = 'DROPDOWN';
            this.values = [
                { display: "Below", value: "below", default: true},                
                { display: "Above", value: "above"},
                { display: "Left", value: "left"},
                { display: "Right", value: "right"}

            ];
        }

        return TooltipPositionInstance;
    }
}