let trimLabelsInstance = null;
module.exports = class trimLabels {

    constructor() {
        if (!trimLabelsInstance) {
            trimLabelsInstance = this;
            //set all other default values

            this.displayAs = 'Trim Labels';
            this.value = '[trimLabels]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return trimLabelsInstance;
    }
}