let wrapInstance = null;
module.exports = class wrap {

    constructor() {
        if (!wrapInstance) {
            wrapInstance = this;
            //set all other default values

            this.displayAs = 'Wrap';
            this.value = 'wrap'
            this.type = 'TOGGLE';
            this.labels = {'true-label': 'Wrap', 'false-label': 'NoWrap'}
            this.values = {'true-value':'wrap', 'false-value': ''}
        }
        return wrapInstance;
    }
}