let xAxisInstance = null;
module.exports = class xAxis {

    constructor() {
        if (!xAxisInstance) {
            xAxisInstance = this;
            //set all other default values

            this.displayAs = 'Show x-Axis';
            this.value = '[xAxis]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return xAxisInstance;
    }
}