let yAxisInstance = null;
module.exports = class yAxis {

    constructor() {
        if (!yAxisInstance) {
            yAxisInstance = this;
            //set all other default values

            this.displayAs = 'Show y-Axis';
            this.value = '[yAxis]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return yAxisInstance;
    }
}