let fxHideInstance = null;
module.exports = class fxHide {

    constructor() {
        if (!fxHideInstance) {
            fxHideInstance = this;
            //set all other default values

            this.displayAs = 'fxHide';
            this.value = '[fxHide]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return fxHideInstance;
    }
}