let fxShowInstance = null;
module.exports = class fxshow {

    constructor() {
        if (!fxShowInstance) {
            fxShowInstance = this;
            //set all other default values

            this.displayAs = 'fxShow';
            this.value = '[fxShow]'
            this.type = 'TOGGLE';
            this.values = {'true-value':'true', 'false-value': 'false'}
        }
        return fxShowInstance;
    }
}