"use strict";
let Component = require('./Component');

module.exports = class Views extends Component {
    constructor(appName, pageName) {

        super({
            name: pageName,
            designerTemplate: '<div class="column drop-view" block-minimize component-label></div>',
            paletteTemplate: pageName,
            componentLabel: "",
            templateUrl: '/articles/#!app-builder-s-user-guide/views'
        });

        super.setType(Component.COMPONENT_TYPE_TITLES.VIEWS.val);
        this._appName = appName;
        this._pageName = pageName;

    }

    static get designerHTML() {
        return '<div class="column drop-view" block-minimize component-label></div>';
    }
};
